import React, { useEffect } from 'react'
import ButtonTest from '../components/Button.js'
import Header from '../components/Header.js';
import Speechbubble from '../components/Speechbubble.js';
import aki3 from '../img/aki3.png';
import Footer from '../components/Footer.js';
import { useState } from 'react'
const info = ["Jag gillar glass", "Jag gillar kebab", "Jag gillar öl"]


const HomeView = () => {
  const [state, setState] = useState('start')

  var clickCount = 0;

  function changeTextACB() {
    clickCount += 1;
    console.log("hej");
    document.querySelector("bubbleText").innerHTML = "hej";
  };

  const hej = () => {

    if(state === 'bubble') {setState('bubble1')}
    if(state === 'bubble1') {setState('bubble2')}
    if(state === 'bubble2') {setState('bubble3')}
  }

  return (

    <div>
      <Header />
      <span className="bubble">

        <ButtonTest addBubble={hej} />

        <Speechbubble text={state==='bubble1' ? info[0]: state ==='bubble2' ? info[1] : state ==='bubble3'} className="slide"/> 

      </span>
      <span className="image">
        <img src={aki3} className="aki"></img>

      </span>


      <Footer />

    </div>


  );

}


export default HomeView
