import React, { useEffect } from 'react'
import ButtonTest from '../components/Button.js'
import Header from '../components/Header.js';
import Speechbubble from '../components/Speechbubble.js';
import aki from '../img/aki.jpg';

const HomeView = () => {
    function simulateNetworkRequest() {
        return new Promise((resolve) => setTimeout(resolve, 2000));
      }

      function LoadingButton() {
        const [isLoading, setLoading] = useState(false);
    useEffect(() => {
        if (isLoading) {
          simulateNetworkRequest().then(() => {
            setLoading(false);
          });
        }
      }, [isLoading]);    
      const handleClick = () => setLoading(true);
  return(

      <div>
        <Header/>
        <ButtonTest onClick={!isLoading ? handleClick : null}/>
        {isLoading ? 'Loading…' : 'Click to load'}
        <div className="mainContent"> <img src={aki} className="aki"></img></div>
        <div className="mainContent"> <Speechbubble/> </div>
      </div>


  );

}
}

export default HomeView
